import React, { useEffect, useState } from "react";
import { FaCrown, FaTrophy } from "react-icons/fa";
import "../App.css"; // Assume styles as shown below are included

const dummyUsers = [
  {
    id: 1,
    name: "PRITESH ðŸ‡®ðŸ‡³",
    points: 1614546,
    avatar: "https://i.pravatar.cc/100?img=1",
  },
  {
