    points: 558378,
    avatar: "https://i.pravatar.cc/100?img=4",
  },
  {
    id: 5,
    name: "MuKkU ðŸ",
    points: 503042,
    avatar: "https://i.pravatar.cc/100?img=5",
  },
  {
    id: 6,
    name: "ðŸ¦‹âœ¨âž•âž–ðŸ’«",
    points: 352250,
    avatar: "https://i.pravatar.cc/100?img=6",
  },
  {
    id: 7,
    name: "CðŸ…¾ï¸RPðŸ…°ï¸T",
    points: 346392,
    avatar: "https://i.pravatar.cc/100?img=7",
  },
  {
    id: 8,
    name: "M.R. âž–Râ–²JPUT....",
    points: 343892,
    avatar: "https://i.pravatar.cc/100?img=8",
  },
  {
    id: 9,
    name: "ðŸ‘‘â€“Ê„Ð½Î¹LðŸ’›ðŸª„",
    points: 321932,
    avatar: "https://i.pravatar.cc/100?img=9",
  },
];

export default function App() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // In real app, fetch sorted users from API
    setUsers(dummyUsers.sort((a, b) => b.points - a.points));
  }, []);

  const topThree = users.slice(0, 3);
  const rest = users.slice(3);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Weekly Contribution Ranking</h1>
      </header>

      <section className="top-three">
        {topThree.map((user, idx) => (
          <div
            key={user.id}
            className={`winner-card ${
              idx === 0 ? "first" : idx === 1 ? "second" : "third"
            }`}
          >
            <div className="winner-avatar">
              <img src={user.avatar} alt={user.name} />
              {idx === 0 && <FaCrown className="crown" />}
            </div>
            <div className="winner-name">{user.name}</div>
            <div className="winner-points">
              <FaTrophy /> {user.points.toLocaleString()}
            </div>
          </div>
        ))}
      </section>

      <section className="leaderboard-section">
        <ul className="leaderboard-list">
          {rest.map((user, idx) => (
            <li key={user.id} className="leaderboard-item">
              <div className="rank-number">{idx + 4}</div>
              <div
                className="user-avatar"
                style={{ backgroundImage: `url(${user.avatar})` }}
              ></div>
              <div className="user-info">
                <div className="user-name">{user.name}</div>
              </div>
              <div className="user-points">
                <FaTrophy className="points-icon" />
                {user.points.toLocaleString()}
              </div>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}
